import React from 'react';
import { BuyButton } from '@moltin/react-shopkit';

const MoltinGateway = require('@moltin/sdk').gateway;

const Moltin = MoltinGateway({
  client_id: process.env.CLIENT_ID
});

class ProductTile extends React.Component {

    constructor(props) {
        super(props);
        console.log(props);
    }

    render() {
        return (
            <div className="card">
            {console.log(this.props.imgId)}
                <div className="container p-4">
                    <img className="card-img-top" src={this.props.imgSource} alt="Card image cap" />
                </div>
                <div className="card-body">
                    <h5 className="card-title">{this.props.name}</h5>
                    <h6 className="card-subtitle mb-2 text-muted">{this.props.desc}</h6>
                    <p className="card-text">${this.props.price / 100}</p>
                    <BuyButton id={this.props.id}>
                        {({ addToCart }) => <button onClick={addToCart}>Add To Cart</button>}
                    </BuyButton>
                </div>
            </div>
        )
    }
}

export default ProductTile
