import Head from 'next/head';
import Layout from '../components/layout';
import ProductTile from '../components/productTile';
import React from 'react';
import { Shopkit } from '@moltin/react-shopkit';
import Navbar from '../components/navbar';

class HomePage extends React.Component {


    render() {
        return (
            <Shopkit clientId={process.env.clientId}>
                <Layout>
                    <Head>
                        <meta name="viewport" content="width=device-width, initial-scale=1, shrink-to-fit=no"></meta>
                        <title>Moltin Ecommerce Site</title>
                        <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/css/bootstrap.min.css" integrity="sha384-ggOyR0iXCbMQv3Xipma34MD+dH/1fQ784/j6cY/iJTQUOhcWr7x9JvoRxT2MZw1T" crossOrigin="anonymous"></link>
                        <link rel="stylesheet" href="https://use.fontawesome.com/releases/v5.8.1/css/all.css" integrity="sha384-50oBUHEmvpQ+1lW4y57PTFmhCaXp0ML5d60M1M7uH2+nqUivzIebhndOJK28anvf" crossOrigin="anonymous"></link>
                        <script src="https://code.jquery.com/jquery-3.3.1.slim.min.js" integrity="sha384-q8i/X+965DzO0rT7abK41JStQIAqVgRVzpbzo5smXKp4YfRvH+8abtTE1Pi6jizo" crossOrigin="anonymous"></script>
                        <script src="https://cdnjs.cloudflare.com/ajax/libs/popper.js/1.14.7/umd/popper.min.js" integrity="sha384-UO2eT0CpHqdSJQ6hJty5KVphtPhzWj9WO1clHTMGa3JDZwrnQq4sF86dIHNDz0W1" crossOrigin="anonymous"></script>
                        <script src="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/js/bootstrap.min.js" integrity="sha384-JjSmVgyd0p3pXB1rRibZUAYoIIy6OrQ6VrjIEaFf/nJGzIxFDsf4x0xIM+B07jRM" crossOrigin="anonymous"></script>
                    </Head>
                    <Navbar curPage="catalog"/>
                    <div className="site-section">
                        <div className="container" style={{height: 'calc(100vh-200px)', minHeight: '580px', zIndex: '9', overflow: 'hidden'}}>
                            <div className="row" style={{height: 'calc(100vh - 200px)'}}>
                                <div className="col-md-6">
                                    <img className="main-img" src="https://www.skuvantage.com.au/wp-content/uploads/2018/02/model01.jpg" alt="main image" />
                                </div>
                                <div className="col-md-6">
                                    <div className="top-margin">
                                        <h5 className="text-info">See All Of Our Collections</h5>
                                        <h1 className="display-4 mb-4">New Arrivals</h1>
                                        <button className="btn btn-lg btn-dark">SHOP NOW</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div clasName="site-section">
                        <div className="full-width">
                            <div className="cat-link grey">
                                <h5>Categories</h5>
                            </div>
                            <div className="cat-link">
                                <h5>Collections</h5>
                            </div>
                            <div className="cat-link grey">
                                <h5>Products</h5>
                            </div>
                        </div>
                    </div>
                    <div className="site-section">
                        <div className="container">
                            <div className="section-title">
                                <h5 className="section-title-header">Discover the Collections</h5>
                            </div>
                        </div>
                    </div>
                </Layout>
                <style jsx>{`
                    .top-margin {
                        margin-top: 40%;
                        text-align: center;
                    }
                    .main-img {
                        max-width: 100%;
                        height: auto;
                    }
                    .full-width {
                        display: flex;
                        justify-content: space-between;
                    }
                    .cat-link {
                        color: white;
                        text-align: center;
                        background-color: #f52968;
                        width: 100%;
                        padding: 18px;
                    }
                    .cat-link h5 {
                        margin-bottom: 0;
                    }
                    .grey {
                        background-color: #f7f7f7;
                        color: black;
                    }
                    .section-title {
                        margin-top: 40px;
                        width: 200px;
                    }
                    .section-title-header {
                        font-size: 2.4rem;
                    }
                `}</style>
            </Shopkit>
        )
    }
}

export default HomePage
